:root {
  font-family: system-ui, Avenir, Helvetica, Arial, sans-serif;
  line-height: 1.5;
  font-weight: 400;

  color-scheme: light dark;
  color: rgba(255, 255, 255, 0.87);
  background-color: #242424;

  font-synthesis: none;
  text-rendering: optimizeLegibility;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

a {
  font-weight: 500;
  color: #646cff;
  text-decoration: inherit;
}
a:hover {
  color: #535bf2;
}

body {
  margin: 0;
  display: flex;
  place-items: center;
  min-width: 320px;
  min-height: 100vh;
}

h1 {
  font-size: 3.2em;
  line-height: 1.1;
}

button {
  border-radius: 8px;
  border: 1px solid transparent;
  padding: 0.6em 1.2em;
  font-size: 1em;
  font-weight: 500;
  font-family: inherit;
  background-color: #1a1a1a;
  cursor: pointer;
  transition: border-color 0.25s;
}
button:hover {
  border-color: #646cff;
}
button:focus,
button:focus-visible {
  outline: 4px auto -webkit-focus-ring-color;
}

@media (prefers-color-scheme: light) {
  :root {
    color: #213547;
    background-color: #ffffff;
  }
  a:hover {
    color: #747bff;
  }
  button {
    background-color: #f9f9f9;
  }
}

import { useState, useEffect } from 'react'
import * as ynab from 'ynab'
import './App.css'

const clientId = import.meta.env.VITE_YNAB_CLIENT_ID
const redirectUri = import.meta.env.VITE_YNAB_REDIRECT_URI

function App() {
  const [accessToken, setAccessToken] = useState('')
  const [isAuthenticated, setIsAuthenticated] = useState(false)
  const [budgets, setBudgets] = useState([])
  const [selectedBudget, setSelectedBudget] = useState('')
  const [recurringCharges, setRecurringCharges] = useState([])
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState('')

  useEffect(() => {
    // Check if we have a token in sessionStorage
    const storedToken = sessionStorage.getItem('ynab_access_token')
    if (storedToken) {
      setAccessToken(storedToken)
      fetchBudgets(storedToken)
      return
    }

    // Check if we're returning from OAuth with a token in the URL hash
    const token = findYNABToken()
    if (token) {
      sessionStorage.setItem('ynab_access_token', token)
      setAccessToken(token)
      // Clean up the URL hash
      window.history.replaceState(null, '', window.location.pathname)
      fetchBudgets(token)
    }
  }, [])

  const findYNABToken = () => {
    const hash = window.location.hash
    if (!hash) return null

    const params = hash
      .substring(1)
      .split('&')
      .reduce((result, param) => {
        const [key, value] = param.split('=')
        result[key] = value
        return result
      }, {})

    return params.access_token || null
  }

  const authorizeWithYNAB = () => {
    if (!clientId) {
      setError('OAuth Client ID is not configured. Please check your .env file.')
      return
    }

    const uri = `https://app.ynab.com/oauth/authorize?client_id=${clientId}&redirect_uri=${redirectUri}&response_type=token`
    window.location.replace(uri)
  }

  const fetchBudgets = async (token) => {
    setLoading(true)
    setError('')

    try {
      const ynabAPI = new ynab.API(token)
      const budgetsResponse = await ynabAPI.budgets.getBudgets()
      setBudgets(budgetsResponse.data.budgets)
      setIsAuthenticated(true)
    } catch (err) {
      setError('Failed to fetch budgets. Please try logging in again.')
      console.error(err)
      // Clear invalid token
      sessionStorage.removeItem('ynab_access_token')
      setAccessToken('')
      setIsAuthenticated(false)
    } finally {
      setLoading(false)
    }
  }

  const handleLogout = () => {
    sessionStorage.removeItem('ynab_access_token')
    setAccessToken('')
    setIsAuthenticated(false)
    setBudgets([])
    setSelectedBudget('')
    setRecurringCharges([])
    setError('')
  }

  const analyzeRecurringCharges = (transactions) => {
    const payeeGroups = {}

    transactions.forEach(transaction => {
      if (transaction.amount >= 0) return // Skip income
      if (transaction.transfer_account_id) return // Skip transfers
      if (!transaction.payee_name || transaction.payee_name === 'Starting Balance') return

      const payeeName = transaction.payee_name
      if (!payeeGroups[payeeName]) {
        payeeGroups[payeeName] = []
      }

      payeeGroups[payeeName].push({
        date: transaction.date,
        amount: transaction.amount,
        memo: transaction.memo,
        account_name: transaction.account_name
      })
    })

    const recurring = []

    Object.entries(payeeGroups).forEach(([payeeName, transactions]) => {
      if (transactions.length < 2) return

      transactions.sort((a, b) => new Date(a.date) - new Date(b.date))

      const amounts = transactions.map(t => t.amount)
      const avgAmount = amounts.reduce((sum, amt) => sum + amt, 0) / amounts.length

      const intervals = []
      for (let i = 1; i < transactions.length; i++) {
        const daysDiff = (new Date(transactions[i].date) - new Date(transactions[i-1].date)) / (1000 * 60 * 60 * 24)
        intervals.push(daysDiff)
      }

      const avgInterval = intervals.reduce((sum, int) => sum + int, 0) / intervals.length
      const hasRegularInterval = avgInterval > 7 && avgInterval < 400

      if (hasRegularInterval) {
        recurring.push({
          payeeName,
          count: transactions.length,
          avgAmount: avgAmount / -1000,
          avgIntervalDays: Math.round(avgInterval),
          lastDate: transactions[transactions.length - 1].date,
          transactions: transactions.map(t => ({
            ...t,
            amount: t.amount / -1000
          }))
        })
      }
    })

    recurring.sort((a, b) => b.avgAmount - a.avgAmount)
    return recurring
  }

  const handleAnalyze = async () => {
    if (!selectedBudget) {
      setError('Please select a budget')
      return
    }

    setLoading(true)
    setError('')

    try {
      const ynabAPI = new ynab.API(accessToken)
      const transactionsResponse = await ynabAPI.transactions.getTransactions(selectedBudget)
      const recurring = analyzeRecurringCharges(transactionsResponse.data.transactions)
      setRecurringCharges(recurring)
    } catch (err) {
      setError('Failed to fetch transactions. Please try again.')
      console.error(err)
    } finally {
      setLoading(false)
    }
  }

  if (!isAuthenticated) {
    return (
      <div className="container">
        <h1>YNAB Recurring Charges Finder</h1>
        <p>Find recurring subscriptions and charges in your budget</p>

        <div className="login-form">
          {loading ? (
            <div className="loading">Connecting to YNAB...</div>
          ) : (
            <>
              <button onClick={authorizeWithYNAB} className="oauth-button">
                Connect with YNAB
              </button>

              <div className="help-text">
                <p>Click the button above to securely connect your YNAB account using OAuth.</p>
                <p>You'll be redirected to YNAB to authorize this app.</p>
              </div>
            </>
          )}
        </div>

        {error && <div className="error">{error}</div>}
      </div>
    )
  }

  return (
    <div className="container">
      <div className="header">
        <h1>YNAB Recurring Charges Finder</h1>
        <button onClick={handleLogout} className="logout-button">
          Logout
        </button>
      </div>

      <div className="budget-selector">
        <label htmlFor="budget">Select Budget:</label>
        <select
          id="budget"
          value={selectedBudget}
          onChange={(e) => setSelectedBudget(e.target.value)}
        >
          <option value="">Choose a budget...</option>
          {budgets.map(budget => (
            <option key={budget.id} value={budget.id}>
              {budget.name}
            </option>
          ))}
        </select>
        <button onClick={handleAnalyze} disabled={loading || !selectedBudget}>
          {loading ? 'Analyzing...' : 'Find Recurring Charges'}
        </button>
      </div>

      {error && <div className="error">{error}</div>}

      {recurringCharges.length > 0 && (
        <div className="results">
          <h2>Found {recurringCharges.length} Recurring Charges</h2>

          {recurringCharges.map((charge, idx) => (
            <div key={idx} className="charge-card">
              <div className="charge-header">
                <h3>{charge.payeeName}</h3>
                <div className="charge-stats">
                  <span className="amount">${charge.avgAmount.toFixed(2)}</span>
                  <span className="frequency">Every ~{charge.avgIntervalDays} days</span>
                  <span className="count">{charge.count} transactions</span>
                </div>
              </div>

              <div className="last-charge">
                Last charge: {new Date(charge.lastDate).toLocaleDateString()}
              </div>

              <details>
                <summary>View all transactions</summary>
                <table>
                  <thead>
                    <tr>
                      <th>Date</th>
                      <th>Amount</th>
                      <th>Account</th>
                      <th>Memo</th>
                    </tr>
                  </thead>
                  <tbody>
                    {charge.transactions.map((txn, i) => (
                      <tr key={i}>
                        <td>{new Date(txn.date).toLocaleDateString()}</td>
                        <td>${txn.amount.toFixed(2)}</td>
                        <td>{txn.account_name}</td>
                        <td>{txn.memo || '-'}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </details>
            </div>
          ))}
        </div>
      )}

      {!loading && recurringCharges.length === 0 && selectedBudget && (
        <div className="no-results">
          <p>No recurring charges found. Try analyzing your budget!</p>
        </div>
      )}
    </div>
  )
}

export default App

